commits.content:
package chihalu.totalitems.event;

import net.fabricmc.fabric.api.client.item.v1.ItemTooltipCallback;
import net.minecraft.client.MinecraftClient;
import net.minecraft.entity.player.PlayerInventory;
import net.minecraft.text.Text;
import net.minecraft.util.Formatting;

import chihalu.totalitems.util.InventoryUtils;

public class TooltipEventHandler {
    
    public static void register() {
        ItemTooltipCallback.EVENT.register((stack, context, tooltipType, lines) -> {
            // クライアントプレイヤーを取得
            MinecraftClient client = MinecraftClient.getInstance();
            if (client.player != null) {
                PlayerInventory inventory = client.player.getInventory();
                
                // 合計アイテム数を取得
                int totalCount = InventoryUtils.getTotalItemCount(inventory, stack);
                
                // インベントリ内のアイテムが1個より多い場合のみ表示
                if (totalCount > stack.getCount()) {
                    lines.add(Text.literal("").append(
                        Text.literal("インベントリ内合計個数: ")
                            .formatted(Formatting.GRAY)
                    ).append(
                        Text.literal(String.valueOf(totalCount))
                            .formatted(Formatting.YELLOW)
                    ));
                }
            }
        });
    }
}
